import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserForm from './components/UserForm';
import UserList from './components/UserList';
import './styles/styles.css';

const App = () => {
    const [users, setUsers] = useState([]);
    const [userToEdit, setUserToEdit] = useState(null);
    const [token, setToken] = useState(null);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');

    const fetchUsers = async () => {
        const response = await axios.get('http://localhost:3001/users', { headers: { Authorization: token } });
        setUsers(response.data);
    };

    useEffect(() => {
        if (isAuthenticated) {
            fetchUsers();
        }
    }, [isAuthenticated]);

    const handleLogin = async (e) => {
        e.preventDefault();
        const response = await axios.post('http://localhost:3001/login', { email, senha });
        setToken(`Bearer ${response.data.token}`);
        setIsAuthenticated(true);
    };

    return (
        <div className="App">
            {!isAuthenticated ? (
                <form onSubmit={handleLogin}>
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Senha"
                        value={senha}
                        onChange={(e) => setSenha(e.target.value)}
                        required
                    />
                    <button type="submit">Login</button>
                </form>
            ) : (
                <>
                    <UserForm userToEdit={userToEdit} fetchUsers={fetchUsers} token={token} />
                    <UserList users={users} fetchUsers={fetchUsers} setUserToEdit={setUserToEdit} token={token} />
                </>
            )}
        </div>
    );
};

export default App;
